var today=new Date;var day=today.getDate();var month=today.getMonth();month+=1;var year=today.getFullYear();switch(month){case 1:month="January";break;case 2:month="February";break;case 3:month="March";break;case 4:month="April";break;case 5:month="May";break;case 6:month="June";break;case 7:month="July";break;case 8:month="August";break;case 9:month="September";break;case 10:month="October";break;case 11:month="November";break;case 12:month="December";break}document.getElementById("month").innerHTML=month;document.getElementById("day").innerHTML=day;document.getElementById("year").innerHTML=year